"use strict";

var form = document.querySelector("#form");
form.addEventListener("submit", function (e) {
	e.preventDefault();
	var values = document.querySelectorAll("#form .control");
	console.log("Проход");
	for (let index = 0; index < values.length; index++) {
		if (values[index].value == "") {
			values[index].classList.add("error");
			console.log("Ошибка, заполнить поле");
			continue;
		}
		if (
			values[index].value != "" &&
			values[index].classList.contains("error")
		) {
			values[index].classList.remove("error");
		}

		console.log(values[index].id, values[index].value);
	}
});
